void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 p0 = p;
    //uv = fract(uv * 4.0) - .5;
    vec3 finalColor = vec3(0);

    float iterations = 4.0;
    for(float i = 0.; i < iterations; i++){
        p = fract(p* 2.) - vec2(0,.5);
        float tb = abs(tan( length(p0) + iTime )) / 5.5;
        float ra = 1.8 + (sin(exp(-length(1./(p0 - p)))) * .3);
        float d = sdCircleWave(p*15.,tb,ra) * exp(-length(p0));
        vec3 col = palette(length(p0) + iTime * 0.1);
        d = abs(d);
        d = .03/d;
        finalColor += col * d ;
      }


    fragColor = vec4(finalColor,1.0);
}
