// Random single-solution maze. Demo for ICP course by ukeshet.
// Improved version of my https://www.shadertoy.com/view/McVGDm
// For documentation, see V4 (https://www.shadertoy.com/view/lfK3Rd)

void mainImage(out vec4 Col, in vec2 Coo)
{
    vec4 s0=texelFetch(iChannel0,ivec2(1,h+1),0);
    float v=12.,t=iTime,x=Coo.x,y=Coo.y,L=iResolution.x,X=L/wf,Y=iResolution.y/float(h),p=mod(iMouse.x+5.*wf+v*iTime,s0[2]+1.);
    vec4 s1=texelFetch(iChannel0,ivec2(int(mod(p,L)),h+1+int(p/L)),0);
    int xy1=floatBitsToInt(s1[0]), xy2=floatBitsToInt(s1[1]);
    vec2 s=mix(vec2(xy1&0xFFFF,(xy1>>16)&0xFFFF),vec2(xy2&0xFFFF,(xy2>>16)&0xFFFF),fract(p)); float d=s[0];
    vec4 m=texelFetch(iChannel0,ivec2(int(mod((x-.5*L)/X+d,float(w))),int(y/Y)),0);
    if (((mod(x+d*X,X)>.8*X)&&(m[0]==0.))||((mod(y,Y)>.8*Y)&&(m[1]==0.))) 
        Col=vec4(.7+.3*sin(3.*T*(x+d*X)/L),.7+.3*cos(5.*T*(x+d*X)/L),.7+.3*cos(T*(x+d*X)/L),1.); 
    else Col=vec4(0.,0.,0.,1.);
    if (length(vec2((x-.5*L)/X-.4,(y/Y)-s[1]-.4))<.3) Col[0]=1.;
}

#define w 35
#define wf 24.
#define h 16
#define pi 3.1415926535
#define T 4.3084699249
// T=2*pi*wf/float(w)

float hash(int seed) {
    int p=23856097;
    int q=seed*p;
    q=(q^(q>>15))*(q|0x5D588B65);
    return fract(float(q)/float(0x7FFFFFFF));
}
