void mainImage( out vec4 fragColor, in vec2 uv )
{
    uv.x = 2.5*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 2.5*(uv.y-0.5*iResolution.y)/iResolution.y;

    float a=float(iFrame)/50.;
    float c=a/2000.;
    float d=a/100.;

    vec3 col = vec3(0.); 
    float v=0.;


    for(float t=-1.; t<=1.; t+=(0.005)){
    float val1 = sin(a*t)*cos(t+d*0.1*a);
    float val2=cos((a+1.)*t)*sin(2.*t+c*a);

    vec2 holder = vec2(val1,val2);

    v=max(v,pow(2.,-5.*length(uv-holder)));   
    }
    col=vec3(v);

    fragColor = vec4(col,1.0);
}
