void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float radius = 0.5;

    // distance to origin
    float dw = length(p);
    float d = abs(dw - radius);

    vec4 borderColor = vec4(0.804, 0.569, 0.620, 1.0);

    float thickness = 3.0f / iResolution.y;

    // roll the border alpha down from 1 to 0 across the border thickness
    vec4 col = vec4(borderColor.rgb, smoothstep(thickness, 0.0, d));

    fragColor = vec4(col.rgb * col.a, col.a);
}
