void mainImage( out vec4 fragColor, in vec2 uv )
{

    float b = floor(float(iFrame));
    if(mod(b,1.)<=0.){

    uv.x = 9.*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 9.*(uv.y-0.5*iResolution.y)/iResolution.y;


    vec3 col = vec3(0.);


    float a=float(iFrame)/100.;

    uv.x=(uv.x)+6.28318530718;
    uv.y=uv.y;


    float val = 1.;
    for(float k=0.; k<=2.; k+=0.1){
    for(float n=0.4; n<=1.; n+=0.5){
    val+=cos((1.+n)*(uv.x+k*cos(a)))*cos((1.+n)*(uv.y+k*sin(a)));

    float v1=pow(2.,-5.*abs(val-1.))+pow(2.,-5.*abs(val-12.))+pow(2.,-5.*abs(val+12.));
    float v2=pow(2.,-5.*abs(val-1.5))+pow(2.,-5.*abs(val-12.5))+pow(2.,-5.*abs(val+12.5));
    float v3=pow(2.,-5.*abs(val-2.))+pow(2.,-5.*abs(val-13.))+pow(2.,-5.*abs(val+13.));
    col+=0.175*vec3(v1,v2,v3);
    }
    }
    fragColor = vec4(col,1.0);
    }else{
    uv = uv / iResolution.xy;
    fragColor = texture(iChannel0, uv);  
    }
}
