void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float t = iTime;

    vec2 uv = fragCoord/iResolution.xy;

    vec3 resultCol = vec3(0.0,0.0,0.0);

    float size = 0.01;

    vec2 center = vec2(0.5,0.5);

    float radius = 0.2;

    float nside = 32.0;

    float ncopies = 5.0;

    for(int i = 1; i < int(nside); i++)
    {
       for(int j = 1; j < int(ncopies); j++)
       {
         float angle = (float(i) / nside) * t * 3.14;
         vec2 pos = center + (float(j) / ncopies)  * (0.2 * cos(t * 0.1)) * vec2(cos(angle), sin(angle)) * 3.0;

         vec3 color = vec3(
              sin(float(j) * 0.1) * 2.0 * sin(t) + 0.1,
              cos(float(j) * 0.5) * 2.0 * cos(t) + 0.1,
              tan(float(j) * 0.9) * 2.0 * sin(t) + 0.1);
        if(length(uv-pos)<size)
        {
           resultCol += color;
        }
       }
     }




    // Output to screen
    fragColor = vec4(resultCol,1.0);
}
