/*
It looks like the SoundCloud API finally stopped working on ShaderToy.
But have no fear, tunes are here, and now back with HLS support as well!

--> How to fix broken SoundCloud URLs on shadertoy <--
1. Open Developer Tools
2. Paste the script below and enjoy the music~

Note: You may have to hit the [â®] rewind button on the audio channel to reset the player.
Also if you want to see how this works, check out the eval-free (but substantially longer) version:
https://gist.github.com/cf/fe578edee8a63977a9f5c47e84925cf4
*/

/*
// START SCRIPT
function patchEffectPassNewTexture(oldScript){
  const newTextureStart = oldScript.indexOf("EffectPass.prototype.NewTexture");
  const newTextureReturn = oldScript.lastIndexOf("return { mFailed: true };");
  if(newTextureStart === -1 || newTextureReturn < newTextureStart) return alert("could not patch EffectPass.prototype.NewTexture, HLS streams will not work");
  const functionBody = oldScript.substring(newTextureStart, newTextureReturn+25)+"\n};";
  eval(
    functionBody
      .replace("texture.audio = document.createElement('audio')","texture.audio = document.createElement('video')")
      .replace("SC.resolve(url.mSrc,", "scResolvePatched(url.mSrc,texture,")
  );
}
function loadHLSJS() {
  if (window._HAS_LOADED_HLS_JS) return Promise.resolve(true);
  return new Promise((resolve) => {
    const script = document.createElement("script");
    script.integrity = "sha384-9v3HcdYrO3D+OPDTjZ40RXocgE4GtXVCd3/mCS62JsM93JXgI1afJVuwjFvsu6ni";
    script.crossOrigin = "anonymous";
    script.onload = () => resolve(window._HAS_LOADED_HLS_JS = true);
    script.src = "https://cdn.jsdelivr.net/npm/hls.js@1.5.17/dist/hls.min.js";
    document.head.appendChild(script);
  });
}
async function soundCloudFix(soundCloudURL) {
  const resp = await fetch("https://shadertoyschls.9z.workers.dev/sc?url="+encodeURIComponent(soundCloudURL));
  const respJson = await resp.json();
  if (respJson.protocol === "hls") await loadHLSJS();
  return respJson;
}
function scResolvePatched(url, texture, _callback, onError) {
  soundCloudFix(url).then((song) => {
    texture.audio.crossOrigin = "anonymous";
    if (song.protocol === "hls" && Hls.isSupported()) { 
      const hls = new Hls();
      hls.loadSource(song.stream_url);
      hls.attachMedia(texture.audio);
    }else{
      texture.audio.src = song.stream_url;
    }
    texture.audio.soundcloudInfo = song;
  }).catch(onError);
}
function runFix() {
  patchEffectPassNewTexture(
    Array.from(document.head.getElementsByTagName("script"))
      .map((x) => x.innerHTML)
      .filter((x) => x.indexOf("EffectPass.prototype.NewTexture") !== -1)[0]
  );
  SC.resolve = function (url, callback, onError) { soundCloudFix(url).then(callback).catch(onError); };
  if (gShaderID) {
    loadShader(gShaderID);
  }
}
runFix();
// END SCRIPT
*/

#define SAMPLE_AT(sp,w) (texture(iChannel1, vec2(floor(sp*subdiv)/subdiv, 0)).x*w)
float qedForAll(vec3 p) {
    float SCALE_QED_2D = SAMPLE_AT(0.7,0.5)-SAMPLE_AT(0.1,0.15)+0.75;
    float d = rotatedSDBox(p, vec3(THICKNESS,0.11*SCALE_QED_2D,0.52*SCALE_QED_2D), vec3(0,-0.1335*SCALE_QED_2D,0.01*SCALE_QED_2D)*2., -0.27925268016);
    d = min(d, rotatedSDBox(p,vec3(THICKNESS,0.09*SCALE_QED_2D,0.58*SCALE_QED_2D), vec3(0,0.1146*SCALE_QED_2D,0.005*SCALE_QED_2D)*2., 0.558505));
    d = min(d, sdBox(p-vec3(0,0.01*SCALE_QED_2D,-0.06*SCALE_QED_2D)*2.,vec3(THICKNESS,0.26*SCALE_QED_2D,0.1*SCALE_QED_2D)));
    d = min(d, sdBox(p-vec3(0,-0.05625*SCALE_QED_2D,0.257*SCALE_QED_2D)*2.,vec3(THICKNESS,0.1125*SCALE_QED_2D,0.04*SCALE_QED_2D)));
    float core = sdBox(p-vec3(0,0.03*SCALE_QED_2D,0.06*SCALE_QED_2D),vec3(THICKNESS,0.6*SCALE_QED_2D,0.5*SCALE_QED_2D));
    d = max(d, core);
    //SCALE_QED_2D = texture(iChannel1, vec2(floor(0.9*subdiv)/subdiv, 0)).y*0.2+SCALE_QED_2D*0.75+0.25;//0.5;
    d = min(d, tubeSDF(p.zxy, 0.78*SCALE_QED_2D, 0.99*SCALE_QED_2D, THICKNESS, 0.01));
    return d;
}

float sdScene(vec3 p) {
  return qedForAll(vec3(p.z, p.x, -p.y)*TWEEN_VALUE*0.5);
}

float rayMarch(vec3 ro, vec3 rd) {
  float depth = MIN_DIST;

  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    float d = sdScene(p);
    depth += d;
    if (d < PRECISION || depth > MAX_DIST) break;
  }

  return depth;
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * EPSILON;
    float r = 1.;
    return normalize(
      e.xyy * sdScene(p + e.xyy) +
      e.yyx * sdScene(p + e.yyx) +
      e.yxy * sdScene(p + e.yxy) +
      e.xxx * sdScene(p + e.xxx));
}

vec3 phong(vec3 lightDir, float lightIntensity, vec3 rd, vec3 normal) {
  vec3 cubemapReflectionColor = texture(iChannel0, reflect(rd, normal)).rgb;

  vec3 K_a = 1.5 * vec3(0.0,0.5,0.8) * cubemapReflectionColor; // Reflection
  vec3 K_d = vec3(1);
  vec3 K_s = vec3(1);
  float alpha = 50.;

  float diffuse = clamp(dot(lightDir, normal), 0., 1.);
  float specular = pow(clamp(dot(reflect(lightDir, normal), -rd), 0., 1.), alpha);

  return lightIntensity * (K_a + K_d * diffuse + K_s * specular);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;

    vec4 col2 = texture(iChannel1, vec2(floor(uv.x*subdiv)/subdiv, 0));
    vec4 icol = texture(iChannel1, vec2(floor((1.0-uv.x)*subdiv)/subdiv, 0));



    float sound = col2.x * height;
    float isound = log(icol.x * height + 1.);

    fragColor = sound > uv.y || isound > 1.0-uv.y? vec4(1):vec4(0);

    float barThickness = (1.0/subdiv)*barAmount;

    fragColor = mod(uv.x, 1.0/subdiv) > barThickness?vec4(0):fragColor;//gaps

    fragColor.xyz *= mix(vec3(0.0, 1.0, 1.0), vec3(0.6, 0.0, 0.5), abs(uv.y - 0.5)*2.0);

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouseUV = iMouse.xy/iResolution.xy;
    if (mouseUV == vec2(0.0)) mouseUV = vec2(0.5); // trick to center mouse on page load

    vec3 lp = vec3(0);
    vec3 ro = vec3(0, 0, 6);
    ro.yz *= rotate2d(mix(-PI/2., PI/2., mouseUV.y));
    ro.xz *= rotate2d(mix(-PI, PI, mouseUV.x));

    vec3 rd = camera(ro, lp) * normalize(vec3(uv, -1));

    vec3 col = vec3(0.);

    float d = rayMarch(ro, rd);

    vec3 p = ro + rd * d;
    vec3 normal = calcNormal(p);

    vec3 lightPosition1 = vec3(1, 1, 1);
    vec3 lightDirection1 = normalize(lightPosition1 - p);
    vec3 lightPosition2 = vec3(-8, -6, -5);
    vec3 lightDirection2 = normalize(lightPosition2 - p);

    float lightIntensity1 = 0.65;
    float lightIntensity2 = 0.3;

    vec3 sphereColor = phong(lightDirection1, lightIntensity1, rd, normal);
    sphereColor += phong(lightDirection2, lightIntensity2, rd, normal);
    sphereColor += fresnel(normal, rd) * 0.4;

    col = mix(col, sphereColor, step(d - MAX_DIST, 0.));

    if(col.x+col.y+col.z>0.5){
      fragColor = vec4(col, 1.0);
    }


}

// Bars from https://www.shadertoy.com/view/slycD3
const int MAX_MARCHING_STEPS = 260;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float PRECISION = 0.0005;
const float EPSILON = 0.00001;
const float PI = 3.14159265359;
#define speed 5.0

const float barAmount = 0.2;//relative thickness of the bars
const float subdiv = 80.0;//number of bars.  
//Try setting subdiv to 800 at barAmount to 1.  Very high resolution information is revealed
//Bar amount 0.5 and subDiv iResolution.x/2. is cool too.

// frequency sweep https://soundcloud.com/audiomix/20hz-20khz-frequency-sweep
//set bar amount to 1.0 and subDiv to width of display

const float height = 0.5;//Maxium height of the bars relative to the screeen.



#define OVERSHOOT(x) tanh(x*x)*(2./exp(x*x*x)+1.)
#define TWEEN_VALUE OVERSHOOT(2.*iTime)

mat2 rot2d(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}
#define THICKNESS 0.1
float tubeSDF(vec3 position, float innerRadius, float outerRadius, float halfHeight, float cornerRadius) {
   vec2 d = vec2(length(position.xz) - (outerRadius + innerRadius) * 0.5, position.y);
   d = abs(d) - vec2((outerRadius - innerRadius) * 0.5, halfHeight) + cornerRadius;
   return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0) - cornerRadius;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float rotatedSDBox(vec3 pIn, vec3 b, vec3 offset, float angle){
    vec3 p=pIn-offset;
    vec4 q = vec4(sin(-angle*0.5), 0.0, 0.0, cos(-angle*0.5)); 
    return sdBox(2.0 * cross(q.xyz, p * q.w + cross(q.xyz, p)) + p, b);   
}
mat2 rotate2d(float theta) {
  float s = sin(theta), c = cos(theta);
  return mat2(c, -s, s, c);
}
mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
    vec3 cd = normalize(lookAtPoint - cameraPos);
    vec3 cr = normalize(cross(vec3(0, 1, 0), cd));
    vec3 cu = normalize(cross(cd, cr));

    return mat3(-cr, cu, -cd);
}
float fresnel(vec3 n, vec3 rd) {
  return pow(clamp(1. - dot(n, -rd), 0., 1.), 5.);
}
