float pass(float i, float ma, float mn) {
    if (i > ma  && i < mn)
        return 1.0;
    else
        return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv *= 10.;

    vec3 col = vec3(0.0);

    col += sin(atan(uv.x, uv.y) * 10.0 - iTime) * (-3.0 + length(uv));

    col += pass(length(uv), sin(atan(uv.x, uv.y) * 5.0 + iTime + 3.0) / 3.0 + 2.0, sin(atan(uv.x, uv.y) * 5.0 + iTime * 1.5) / 3.0 + 3.); 

    col *= vec3(1.0, 0.8, 0.5);

    col.xy *= col.xy;

    col = col * 0.25;

    //col = 1.0 - col;

    fragColor = vec4(col,1.0);
}
