#define PI 3.14159265359
#define BALL_RADIUS 0.3 

vec3 basketballPattern(vec3 p) {
    vec3 color = vec3(1.0, 0.5, 0.0); 
    if (abs(p.x) < 0.05 || abs(p.z) < 0.05 || abs(length(p.xz) - BALL_RADIUS) < 0.02) {
        color = vec3(0.0); 
    }

    return color;
}

float ballBounce(float time) {
    return abs(sin(time * 3.0)) * 0.5 + BALL_RADIUS; 
}

vec3 basketballCourtColor(vec3 p) {

    if (abs(p.y) < 0.05) {
        return vec3(1.0); 
    }

    float woodVariation = abs(sin(p.x * 4.0) * sin(p.z * 4.0) * cos(p.y * 2.0));  
    return mix(vec3(0.6, 0.3, 0.1), vec3(0.8, 0.5, 0.3), woodVariation); 
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0; 
    uv.x *= iResolution.x / iResolution.y; 

    vec3 cameraPos = vec3(0.0, 2.0, -5.0);
    vec3 cameraTarget = vec3(0.0, 0.0, 0.0);
    vec3 forward = normalize(cameraTarget - cameraPos);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);

    vec3 rayDir = normalize(uv.x * right + uv.y * up + forward);

    float bounceHeight = ballBounce(iTime);
    vec3 ballCenter = vec3(0.0, bounceHeight, -3.0);

    vec3 oc = cameraPos - ballCenter;
    float a = dot(rayDir, rayDir);
    float b = 2.0 * dot(oc, rayDir);
    float c = dot(oc, oc) - BALL_RADIUS * BALL_RADIUS;
    float discriminant = b * b - 4.0 * a * c;

    vec3 color = basketballCourtColor(cameraPos + rayDir * 5.0); 

    if (discriminant > 0.0) {
        float t = (-b - sqrt(discriminant)) / (2.0 * a);
        vec3 hitPoint = cameraPos + t * rayDir;
        vec3 normal = normalize(hitPoint - ballCenter);

        vec3 localHit = hitPoint - ballCenter;
        color = basketballPattern(localHit);
    }

    fragColor = vec4(color, 1.0);
}
