#define rot(a) mat2(cos(a + vec4(0, 33, 11, 0)))
#define r34(p) fract(29. * sin(p.xyzx + p.xyzx) * sin(p.yzxx))
#define Q(z) P((z), .0, 0., .0, 2., 0.)
#define P(z, a, b, c, d, e) vec3(sin(z * a) * b, cos(z * c) * d - e, z)
void mainImage(out vec4 O, vec2 u){
    vec3  p, r = iResolution, C;       
    float i, a, s, t = iTime * 2.;

    O *= a; 
    while(i++ < 2e2){
        s = a + t;

        p = acos(cos(
              vec3((u + u - r.xy) / r.y * a / 4.
                     * mat2(cos(vec4(0, 11, 33, 0) + t / 18.))
                     + vec2(  
                         cos(t * .3) - cos(s * .3),
                         sin(t * .4) - sin(s * .4)
                   ) * cos(t * .2) / 2., s
              )

        ));

        int j ;
        while(j++ < 2) 

               p=   r34(p).xyz;
            p = abs(3. * p),

            p = p.x < p.y ? p.zxy : p.zyx,
            p -= vec3(3, 1, 2);

        C = .1 * cos(s * 3.14 / 8. + vec3(2, 4, 6));



        a += s = abs(length(p.xz) / 23. - .04) + 2e-5;

        O.rgb += C * C / exp(i * i / 24. * s);
    }
}
