// Fork of "harmonic waves" by FabriceNeyret2. https://shadertoy.com/view/XfX3Dn
// 2023-12-19 16:15:48

// -2Â by Observer


vec3 hsb2rgb( in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return (c.z * mix( vec3(1.0), rgb, c.y));
}


//#define S(v) smoothstep( 1.5, 0., abs(v)/fwidth(v) )
#define S(P) smoothstep( 5./R.y*i, 0., abs( length(P) - 1. ) )
#define PI  3.14159265359
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.2*( u+u - R ) / R.y, V;

    float i=1., v=i;   

    O = vec4( S(U) );                                // circle
    if ( dot(U,U) < i )                               // inside only
        for( ; i++ < 9.;  v /= 1.2 )                  // foreach harmonic
            V = U*i, 
            V.x = mod( V.x + i + sign(V.y), 4.) - cos((iTime))*2.,// arc center
            O = max(O, S(V)*v ) ;                    // draw arc, grey=v
    //add some color
    if ( dot(U,U) < 0.99 )
      O *= vec4(hsb2rgb(vec3(PI/9.*U.x,0.9,0.9)),1.0);

}
