#define PI 3.14159
#define R iResolution
#define dt iTime

const vec3
black=vec3(0.),
white=vec3(1.),
A=vec3(.5),
B=vec3(.5),
C=vec3(1.),
D=vec3(0,.2,.4);
vec3 palette(vec3 t){return A+B*cos(2.*PI*(C*t+D));}
vec3 palette(float t){return palette(vec3(t));}

mat2 rot2D(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

float f(vec2 uv){
    float v,r,a,d,t,c;
    t=(sin(.5*dt)+1.)/2.;
    c=10.;
    a=atan(uv.y,uv.x);
    d=length(uv);
    d=abs(sin(d*c+dt));
    r=abs(cos(a*c))+(t-1.);
    v=(t*.3+.2)/abs(r-d);
    v=mix(v,smoothstep(.75,.8,v)*.9,(smoothstep(.2,.8,v)/3.));
    return v;
}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv = (2.*I-R.xy)/R.y;
    float t=dt*.35;
    vec3 col=palette((.5*cos(uv.xyx+t)*sin(uv.yxy+t)));
    col=mix(black,col,f(uv));
    C = vec4(col,1.0);
}
