//stuff
vec3 palette( in float t )
{
    vec3 a = vec3(0.2, 0.7, 0.3);
    vec3 b = vec3(0.0, 7.5, 0.5);
    vec3 c = vec3(2.0, 1.0, 7.0);
    vec3 d = vec3(0.59, 0.10, 0.9);
    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 rot2D(float angle){
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s, s, c); 
}


float sdBox( vec3 p , vec3 b){
    vec3 q = abs(p) - b;

    return length(max(q,0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

//draw shapes
float map(vec3 p){
    p.z += iTime * 0.1;
    vec3 q = p;
    q = sin(fract(p) -0.5);

    float box = sdBox(q, vec3(0.005));

    return box;
    }

//run
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy * 2.0 - iResolution.xy)/iResolution.y;

    //initialization
    //ray origin
    vec3 ro = vec3(0.0, 0.0, -3.0);
    //ray direction
    vec3 rd = normalize(vec3(uv, 1.0));
    vec3 col = vec3(0.0);

    float t = 0.0;

    //mouse rotation y
    ro.yz *= rot2D(-m.y);
    rd.yz *= rot2D(-m.y);

    //mouse rotation x
    ro.xz *= rot2D(-m.x);
    rd.xz *= rot2D(-m.x);


    //ray marching
    for(int i = 0; i<80; i++){
        vec3 p = ro + rd * t;

        float d = map(p);

        t += d;

        if (d < 0.01 || t > 100.) break;
    }
    col = palette(t*0.04);

    fragColor = vec4(col, 1);
}
