//size of the rendered pixels in real pixels
const float size = 5.0;

//how often the noise should change per second
const float rate = 2.0;


float rng(float seed, float randomizer) {
    uint x = uint(seed);

    x ^= x >> 16;
    x *= 4123755803u + uint(randomizer);
    //x *= 4123755803u;
    x ^= x >> 15;
    x *= 2221713035u;
    x ^= x >> 16;

	float randomized = float(x) / 4294967296.0;

    return randomized;
}

 vec3 easyRng(float fragIndex) {
     vec3 color = vec3(0);

    color.r = rng(fragIndex, iTime * rate + 0.0);
    color.g = rng(fragIndex, iTime * rate + 1.0);
    color.b = rng(fragIndex, iTime * rate + 2.0);

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 fakeFragCoord = floor(fragCoord / size);

    float fragIndex = (fakeFragCoord.y * iResolution.x) + fakeFragCoord.x;

    // Output to screen
    fragColor = vec4(easyRng(fragIndex), 1.0);
}
