const float time_mult = 1.5;


float random(vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

// Converts a color from sRGB gamma to linear light gamma
vec3 to_linear(vec3 sRGB)
{
    bvec3 cutoff = lessThan(sRGB, vec3(0.04045));
    vec3 higher = pow((sRGB + vec3(0.055)) / vec3(1.055), vec3(2.4));
    vec3 lower = sRGB / vec3(12.92);

    return mix(higher, lower, cutoff);
}

#define USE_CONTRAST_RATIO_APPROACH

vec3 sample_image(vec2 uv)
{
    if (int(iTime * time_mult) % 2 == 0)
        return texture(iChannel2, uv).rgb;
    return texture(iChannel3, uv).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 curr = sample_image(uv);
    vec3 buffer1 = texture(iChannel0, uv).rgb;
    vec3 buffer2 = texture(iChannel1, uv).rgb;

    float time_factor = 1.0 - (iTime * time_mult - floor(iTime * time_mult));

#ifdef USE_CONTRAST_RATIO_APPROACH
    vec3 linearized_curr = to_linear(curr);
    vec3 linearized_buffer1 = to_linear(buffer1);
    //vec3 linearized_buffer2 = to_linear(buffer2);

    float luminance_curr = dot(linearized_curr, vec3(0.2126, 0.7152, 0.0722));
    float luminance_buffer1 = dot(linearized_buffer1, vec3(0.2126, 0.7152, 0.0722));
    //float luminance_buffer2 = dot(linearized_buffer2, vec3(0.2126, 0.7152, 0.0722));

    float curr_contrast_ratio = (max(luminance_curr, luminance_buffer1) + 0.05) / (min(luminance_curr, luminance_buffer1) + 0.05);
    //float prev_contrast_ratio = (max(luminance_buffer1, luminance_buffer2) + 0.05) / (min(luminance_buffer1, luminance_buffer2) + 0.05);

    float curr_diff = clamp(curr_contrast_ratio * time_factor / 1.2, 0.02, 1.0);
    float prev_diff = 0.0;//clamp(prev_contrast_ratio * time_factor / 12.0, 0.0, 0.35);
#else
    float curr_diff = clamp((length(curr - buffer2) + 0.1) * time_factor / 1.5, 0.02, 1.0);
    float prev_diff = clamp((length(buffer1 - buffer2) + 0.1) * time_factor / 7.0, 0.0, 0.35);
#endif

    vec3 color = mix(curr, buffer2, curr_diff);
    color = mix(color, buffer1, prev_diff);
    fragColor = vec4(color, 1.0);
}
