void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = vec2(sin(iTime * 1.5) * 0.5 + 0.5, 0.0);

    vec4 scr = texture(iChannel0, uv);
    float srcX = scr.x;
    vec3 srcColor = scr.yzw; 

    vec4 res = texture(iChannel1, uv);
    float seedX = res.x;
    vec3 seedColor = res.yzw;
    vec3 col = seedColor;

    fragColor = vec4( m.x > uv.x ? srcColor : col, 1.0);
    fragColor += step(abs(m.x - uv.x), 0.002);      
}

#define max_step 8.0

float greenScreen(vec3 x){
	vec2 c = step(0.4, vec2(x.g - x.r * x.y, x.g));

    return c.x * c.y;
}


vec4 jumpFlood(sampler2D channel, sampler2D source, vec2 fragCoord, vec2 res, float stepSize, int iFrame) {
    float stepwidth = pow(stepSize, 2.0) * 0.5;

    float bestX = 0.0;
    float bestDist = 99999.0;
    vec3 bestColor = vec3(0.0);
    int win = 8;
    vec4 src =  texelFetch(source, ivec2(fragCoord), 0);


    if (src.x != 0.0 && src.y != 0.0) { return src; }

    for (int x = -win; x <= win; x++) {
        vec2 sampleCoord = fragCoord + vec2(x, 0) * stepwidth;
        vec4 data = texelFetch(channel, ivec2(sampleCoord), 0);
        float seedX = data.x;
        vec3 seedColor = data.yzw;

        float diff = seedX - fragCoord.x;
        float dist = abs(diff);
        if (dist < bestDist) {
            bestDist = dist;
            bestX = seedX;
            bestColor = seedColor;
        }

    }

    return vec4(bestX, bestColor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float iTime = 0.0;
    vec2 m = vec2( sin(float(iFrame) / 50.0) * 0.1, cos(float(iFrame) / 50.0) * 0.1);

    vec3 tex = texture(iChannel0, (uv + m * 0.5) * 1.25 - vec2(0.2, -0.0)).rgb;
    vec3 bg = texture(iChannel1, vec3(uv + m + vec2(0.0, -0.35), 0.7)).rgb;
    float mask = greenScreen(tex);
    vec3 col = mix(tex, bg, mask);


    fragColor = vec4(fragCoord.x * mask, bg.xyz * mask);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 res = iChannelResolution[0].xy;
    vec4 c = jumpFlood(iChannel0, iChannel0, fragCoord, res, max_step, iFrame);
    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 res = iChannelResolution[0].xy;
    vec4 c = jumpFlood(iChannel0, iChannel1, fragCoord, res, max_step * 0.5, iFrame);
    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 res = iChannelResolution[0].xy;
    vec4 c = jumpFlood(iChannel0, iChannel1, fragCoord, res, max_step * 0.25, iFrame);
    fragColor = c;
}
