void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 bgColor = vec3(1.0, 0.0, 0.0);

    float darknessMult = 1.0;
	float transparentMult = 1.0;
	if (uv.x < 0.1) {
		darknessMult = smoothstep(0.0, 0.1, uv.x);
		transparentMult = smoothstep(0.0, 0.1, uv.x);
	}
	if (uv.x > 0.9) {
		darknessMult = smoothstep(1.0, 0.9, uv.x);
		transparentMult = smoothstep(1.0, 0.9, uv.x);
	}

    fragColor = vec4(bgColor * darknessMult, 1.0 * transparentMult);
}
