#define t1 137 // some random integer
#define t2 3  // 1, 2, 3 for nice results, bigger for chunks
#define t3 7  // has to be odd for a maze, even for symmetric patterns
// Use replacement for bitwise AND: flags % n+1 / n == 1

int pattern(in vec2 p, in int flags){
	ivec2 I = ivec2(p);
    int r = I.x & 1;
    int j = I.y & 1;

    if(flags%2==1)
        r ^= j;

    if(flags%4/2==1)
        r &= j;

    I /= t2;
    I *= t1;
    I.x ^= I.y;

    if(flags%8/4==1)
        I.x %= t3;

    r &= I.x;

    if(flags%16/8==1)
        r ^= j;

    return r;
}

void mainImage(out vec4 C, vec2 p) {
    int r = pattern(p * 0.25, iFrame/60);
    C = vec4(r,r,r,1.0);
}
