void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u_uv = fragCoord/iResolution.xy;
    vec2 uv = u_uv*2.-1.;

    float twist_factor = 20.*pow(sin(iTime),3.);

    float r = length(uv);
    float theta = atan(uv.y, uv.x)+r*twist_factor;
    vec2 c_uv = r*vec2(cos(theta), sin(theta));
    vec2 cu_uv = c_uv/2.+0.5;

    // Time varying pixel color
    vec3 col = texture(iChannel0, cu_uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
