// CIE Standard Illuminant D65  
vec3 cied65() {
  return vec3(
    cied65i(int(wl.x)),
    cied65i(int(wl.y)),
    cied65i(int(wl.z))); }

// SRGB Color Matching Functions
vec3 srgbcmf() {
  return
    srgbcmfi(int(wl.x)) * Lo.x +
    srgbcmfi(int(wl.y)) * Lo.y +
    srgbcmfi(int(wl.z)) * Lo.z; }

vec3 quadsorb(
  float c,
  float w) {
  return 1.0 + pow(abs((wl - c) / w), vec3(2.0)); }

