float df(vec3 pos) { return distM(pos).x; }

vec3 nf(vec3 pos)
{
    vec2 d=vec2(eps,0);
    float d0=df(pos);
    return normalize(vec3(df(pos+d.xyy)-d0,
                df(pos+d.yxy)-d0,
                df(pos+d.yyx)-d0)/eps);

}

float pickone(vec3 v, float pick) {
  if (pick < 1.0 / 3.0) {
    return v.x; }
  else if (pick < 2.0 / 3.0) {
    return v.y; }
  else {
    return v.z; } }

void scene(vec3 ro, vec3 rd) {
  vec3 throughput = vec3(1.0);
  float t = 0.0;
  for (int i = 0; i < 256; ++i) {
    vec3 p = ro + t * rd;
    float dmin = 1.0 / zero;
    float dhelix;
    float Rhelix = 3.0;
    float Dhelix = dot(p.xz, p.xz) - pow(Rhelix, 2.0);
    //vec4 ghelix;
    if (Dhelix < 0.0 || dot(-p.xz, normalize(rd.xz)) > sqrt(Dhelix)) {
      //ghelix = dfg(ivg(p));
      //dhelix = ghelix.w;
      dhelix = df(p);
      dmin = min(dmin, dhelix); }
    if (dmin == 1.0 / zero) {
      vec3 room = srgb_igamma(texture(iChannel3, rd).bgr);
      Lo += throughput * (0.0
        + room / (1.2 - room)
        + (dot(rd, sun_dir) > sun_cosine ? 1000.0 * cied65() : vec3(0.0)));
      break; }
    // otherwise (dmin == dhelix)
    if (dmin > eps) {
      t += dmin;
      continue; }
    vec3 n = nf(p); //normalize(ghelix.xyz);
    if (dot(rd, n) > 0.0) {
      t += max(dmin, eps);
      continue; }
    float m = distM(p).y;
    ro = p; t = 0.0;
    vec3 nor = texBump(iChannel1, p * 4.0, n, m > 0.5 && m < 1.5 ? 0.001 : 0.004);
    vec3 albedo = 
      m > 1.5 ? 0.2 / quadsorb(590.0, 130.0) :
      m > 0.5 ? 0.8 / quadsorb(480.0, 200.0) :
      0.7 / quadsorb(760.0, 100.0) + 0.15 / quadsorb(0.5 / 800.0, 200.0);
    vec3 ior = vec3(m > 1.5 ? 2.5 : 2.2);
    vec3 specularity = vec3(
      fresnel_reflectance(rd, nor, ior.x),
      fresnel_reflectance(rd, nor, ior.y),
      fresnel_reflectance(rd, nor, ior.z));
    vec3 power = vec3(m > 0.5 ? 20000.0 : 5000.0);
    vec3 spec_dir = reflect(rd, nor);
    float pick = unitrand();
    float spec_dist_specularity = sqrt(pickone(specularity, pick));
    float spec_dist_power = pickone(power, pick) / 2.0;
    vec3 sun_rd = axial(sun_dir, 1.0 - sun_cocosine * unitrand());
    vec3 spec_rd = power_icdf(spec_dir, spec_dist_power);
    vec3 hemi_rd = power_icdf(n, 1.0);
    float sun_prob = dot(sun_rd, n) > 0.0 ? 1.0 - spec_dist_specularity : 0.0;
    float spec_prob = dot(spec_rd, n) > 0.0 ? spec_dist_specularity : 0.0;
    float hemi_prob = 1.0 - spec_dist_specularity;
    float prob_sum = sun_prob + spec_prob + hemi_prob;
    if (prob_sum == 0.0) {
      break; }
    sun_prob /= prob_sum;
    spec_prob /= prob_sum;
    hemi_prob /= prob_sum;
    float roll = unitrand();
    vec3 rd1 =
      roll < sun_prob ? sun_rd :
      roll < sun_prob + spec_prob ? spec_rd :
      hemi_rd;
    float sun_dist = rd1 == sun_rd || dot(rd1, sun_dir) >= sun_cosine ? sun_probability_density : 0.0;
    float spec_dist = power_pdf(spec_dir, spec_dist_power, rd1);
    float hemi_dist = power_pdf(n, 1.0, rd1);
    float dist = sun_prob * sun_dist + spec_prob * spec_dist + hemi_prob * hemi_dist;
    if (dist == 0.0) {
      break; }
    vec3 coef = blinn_phong_coef(rd1, rd, nor, albedo, specularity, power);
    throughput *= coef / dist;
    rd = rd1; } }

void mainImage(out vec4 o, in vec2 xy) {
  srand(ivec3(xy, iFrame + int(iFrameRate * iTime)));
  time = 0.0; //(iTime + unitrand() * iTimeDelta);
  float wl_min = 360.0;
  float wl_max = 800.0;
  float wl_rng = wl_max - wl_min;
  vec2 wl_pts = wl_min + vec2(1.0, 2.0) * wl_rng / 3.0;
  wl = mix(
    vec3(wl_min, wl_pts.x, wl_pts.y),
    vec3(wl_pts.x, wl_pts.y, wl_max),
    vec3(unitrand(), unitrand(), unitrand()));
  mat4 view =
    rotate(dy, 2.9) *
    rotate(dx, -0.2) *
    translate(vec4(0.0, 0.0, -5.5, 0.0));
  vec2 lenspoint = 0.005 * sqrt(unitrand()) 
    * dir2(6.28318530718 * boxrand());
  vec3 ro = vec3(lenspoint, 0.0);
  vec3 rd = normalize(vec3(
    (xy - 0.5 * iResolution.xy + vec2(boxrand(), boxrand()))
    / (iResolution.y * 2.3)
    - (lenspoint / 4.0),
    1.0));
  ro = (view * vec4(ro, 1.0)).xyz;
  rd = (view * vec4(rd, 0.0)).xyz;
  scene(ro, rd);
  o = vec4(srgbcmf(), 1.0);
  if (iFrame != 0) {
    o += texelFetch(iChannel0, ivec2(xy), 0); } }
