#define IF(v) if (uvIndex == floor(vec2(mod(v,colRow.x), abs((mod(v,2.)-1.) * colRow.y)))) 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x,sizeOfSnake = 8.;

    vec2 colRow = vec2(14,8), //size of grid
         UV = fragCoord.xy/iResolution.xy,
         grid = UV*colRow,
         uvIndex = floor(grid),
         uv = fract(grid) - 0.5,
         abuv = abs(uv);

    vec4 iCol = vec4(0.,0.3,0.25,1),
         fCol = vec4(0.4,0.3,0.25,1), 
         eyesCol = vec4(1.,0.3,0.25,1), 
         bgCol = fragColor = vec4(0.8,0.94,1.,1); 

    //snake head
    IF ( iTime ) {
        fragColor = abuv.x > 0.1 && abuv.x < 0.25 && uv.y > 0.1 && uv.y < 0.25 ? eyesCol
        : length(uv) < 0.4 ? iCol
        : bgCol;
    }
    //snake body
    for( ; x++ < sizeOfSnake; ){
        IF ( iTime-(colRow.x/colRow.y)/ colRow.x * x ) { 
            vec4 mCol = mix(iCol,fCol,x/sizeOfSnake);
            fragColor = x < sizeOfSnake || abuv.x > 0.3 && abuv.y > 0.3 ?
            length(uv) > 0.4 ? bgCol : mCol : mCol;
        }
    }
}
