#define rot(x) mat2(cos(x+vec4(0,11,33,0)))
#define H(h)  (cos(h/2.+vec3(1,2,3))*.8 + vec3(0,0,-.1)+.1 )
#define R iResolution

#define ex exp(w.x)
#define cy cos(w.y)
#define sy sin(w.y)
#define cz cos(w.z)
#define sz sin(w.z)
#define cw cos(w.w)
#define sw sin(w.w)

void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, R.y, 0.));

    float totdist=0., tt=iTime, t=0.;

    for (float i=0.; i<50.; i++) {

        vec4 p = vec4( rd*totdist);

        p.z  -= 4.;
        p.xz *= rot( 1.6*(1.+.2*sin(tt/3.3) ) ) ;
        p.xy *= rot( -.1 * (1.+.2*sin(tt/3.)) ) ;
        p.yz *= rot(tt/5.);

        vec4 w=vec4(0) , dz, z;


        for (float j=0.; j<7.; j++) {

                z = vec4( 
                        ex*(cy*cz*cw - sy*sz*sw),
                        ex*(sy*cz*cw + cy*sz*sw),
                        ex*(cy*sz*cw - sy*cz*sw),
                        ex*(sy*sz*cw + cy*cz*sw)
                        );

                //iterated derivative here instead of just scale factor
                dz = vec4(z.x*dz.x-z.y*dz.y-z.z*dz.z-z.w*dz.w,
                          z.x*dz.y+z.y*dz.x+z.z*dz.w-z.w*dz.z,
                          z.x*dz.z-z.y*dz.w+z.z*dz.x+z.w*dz.y,
                          z.x*dz.w+z.y*dz.z-z.z*dz.y+z.w*dz.x);

                dz.x += 1.;

                w = exp(-z*z) + p ;  //added exp(-z^2) here

        }

        float ddz = clamp(dot(dz,dz),1e-3,1e6);
        float ddw = clamp(dot(w,w),.01,1e4);

        float dist = sqrt(sqrt(ddw/ddz)); // * log(ddw); //variation

        float stepsize = dist/2.;     
        totdist += stepsize;

        c += .3*H(log(ddz) ) * exp(-i*i*dist*dist*1.4);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
