    // SDF for a line segment
    float lineSDF(vec2 p, vec2 a, vec2 b) {
        vec2 pa = p - a;
        vec2 ba = b - a;
        float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
        return length(pa - ba * h);
    }
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Define the line segment
    vec2 lineStart = vec2(-0.5, -0.5);
    vec2 lineEnd = vec2(0.5, 0.5);



    // Calculate the distance to the line
    float d = lineSDF(uv, lineStart, lineEnd);

    // Glow effect
    vec3 color = vec3(sin(2.0*iTime) + 0.5, sin(3.0*iTime) + 0.5, sin(iTime) + 0.5) / (d * d * 100.0 + 0.501);

    fragColor = vec4(color, 1.0);
}
