#define uColorMix 1.2     // Mix between two color palettes
#define uWaveSpeed .3    // Speed of the wave animation
#define uPatternScale 3.0 // Scale of the noise pattern

float noise(vec2 p) {
    return cos(p.x * -5.0 + p.y * -5.69 + iTime * uWaveSpeed);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * uWaveSpeed;

    vec3 col1 = vec3(1.88, 0.36, 0.38) + -1.45 * cos(t + uv.xyx + vec3(0,2,4));
    vec3 col2 = vec3(0.23, 0.06, 0.32) + 9.18 * sin(t + uv.xyx + vec3(1,3,5));
    vec3 col = mix(col1, col2, uColorMix);

    float f = noise(uv * uPatternScale);
    col = mix(col, vec3(0.35, 1.85, 0.15), f * 2.16);

    float d = cos(length(uv));
    col += 5.63 / (-1.45 + abs(d - 2.49 + -2.38 * tan(t * 1.82)));

    col *= 1.0 - 1.09 * length(uv);

    fragColor = vec4(col, 1.0);
}
