void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = fragCoord / iResolution.xy;

  vec4 col = texture(iChannel0, uv);

  fragColor = col;
}

vec3 draw(in vec2 fragCoord)
{
  vec2 center = iResolution.xy * 0.5;

  float speed = iTime * 0.1;

  float twoPI = 2.0 * 3.14;

  float angle = mod(speed * twoPI, twoPI);

  vec2 xform = vec2(sin(angle), cos(angle));

  vec2 orbit = iResolution.yy * 0.4;

  vec2 origin = orbit * xform + center;

  float size = iResolution.y * 0.125;

  float dist = distance(fragCoord, origin);

  vec3 color = vec3(1.0, 0.5, 0.1) * 0.01;

  return color * size / dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = fragCoord / iResolution.xy;

  vec4 col = texture(iChannel0, uv);

  col.rgb *= 0.95; // trail length (dissolve)

  col.rgb += draw(fragCoord);

  col.rgb = min(col.rgb, vec3(1.0));

  fragColor = col;
}
