#define t iTime

void mainImage(out vec4 o, vec2 u) {    
    vec2 r = iResolution.xy; 
         u = (u - r.xy / 2.) / r.y;

    o -= o;
    vec3 D = normalize(vec3(u, 2)), f, q, 
         p = vec3(0, 0, -5);
    float i, j, s, d = 1.;

    if(u.x > 0.){
        if(u.y > 0.){
            while(i++ < 120. && d > .01){
                d = length(p * (cos(t)*.5+.5)) - .5;

                s = mix(.17, .005, .7);
                d *= s * .3;

                j = 1.;
