#define PI 3.14159265358979323846


float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 truchetPattern(in vec2 _st, in float _index){
    _index = fract(((_index-0.5)*2.0));
    if (_index > 0.75) {
        _st = vec2(1.0) - _st;
    } else if (_index > 0.5) {
        _st = vec2(1.0-_st.x,_st.y);
    } else if (_index > 0.25) {
        _st = 1.0-vec2(1.0-_st.x,_st.y);
    }
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy) / iResolution.y * 5. + iTime * 1.5;

    vec2 ipos = floor(uv);  // integer
    vec2 fpos = fract(uv);  // fraction

    vec2 tile = truchetPattern(fpos, random( ipos + floor(iTime / PI + 1.)));

    float color = 0.0;
    // Circles
    color = step(length(tile - .5 * vec2(sin(iTime) + 1.)),0.6) -
              step(length(tile - .5 * vec2(sin(iTime) + 1.)),0.4) +
             step(length(tile + .5 * vec2(sin(iTime) - 1.)),0.6) -
              step(length(tile + .5 * vec2(sin(iTime) - 1.)),0.4);


    fragColor = vec4(vec3(color),1.0);
}
