void mainImage( out vec4 col, in vec2 uv )
{
    float skip=3.;
    float n=0.5+mod(floor(float(iFrame)/(240.))/2.,4.);
    col=vec4(0.);
    if(mod(floor(float(iFrame)),skip)<=0.){
    if(length(uv-0.5*iResolution.xy)<0.5*iResolution.y){
    col=vec4(0.);
    uv.x = 1.15*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 1.15*(uv.y-0.5*iResolution.y)/iResolution.y;
    float a = floor(float(iFrame))/(240.);

    float ang = atan(uv.y/uv.x);
    float rate=1.;

    float r1=0.;
    float r2=0.;
    float r3=0.;
    float x1=0.;
    float y1=0.;
    vec2 holder=vec2(0.);
    float dist1=0.;
    float dist2=0.;
    float dist3=0.;

    for(float q=0.; q<=2.; q+=0.1){
    for(float theta=rate*a; theta<=rate*a+2.*3.1415; theta+=3.141592/50.){
    float g=-10.;
    float h=0.025;
    float b=0.6;
    r1=1./(1.86+q)*sin(3.141592/2.-sin(-n*theta+q*3.141592/2.));
    x1 = r1*cos(theta);
    y1 = r1*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(0.,pow(2.,g*dist1),0.,0.);
    }



    r2=1./(1.86+q)*sin(3.141592/2.-sin(n*theta+q*3.141592/2.));
    x1 = r2*cos(theta);
    y1 = r2*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(0.,0.,pow(2.,g*dist1),0.);
    }

    h=0.005;
    r3=1./(2.6*(1.86+q))*sin(3.141592/2.-sin(-n*theta+q*3.141592/2.))-0.5/2.6;
    x1 = r3*cos(theta);
    y1 = r3*sin(theta);
    holder=vec2(x1,y1);
    dist1=length(holder-uv);
    if(abs(uv.x+uv.y-x1-y1)+abs(uv.x-uv.y-x1+y1)<h){
    col+=b*vec4(pow(2.,g*dist1),0.,0.,0.);
    }
    }


    }
    }
    }else{
    uv = uv / iResolution.xy;
    col = texture(iChannel0, uv); 
    }
}
