void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 center =(2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);
    float radius = 0.5;


    center += sin(center*iTime)*0.1;

    center += smoothstep(0.0,1.0,center);

    vec3 color = vec3(center.x*radius , center.y*radius, 0.5);

    fragColor = vec4(color,0.0);
}
