void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;

    vec2 cir1 = vec2(0.5 + 0.2 * sin(iTime), 0.5 + 0.3 * cos(iTime)); 
    vec2 cir2 = vec2(0.5 + 0.2 * cos(iTime), 0.5 + 0.3 * sin(iTime)); 
    vec2 cir3 = vec2(0.5, 0.5);

    vec3 col1 = vec3(cir1.x, cir1.y, 1.);
    vec3 col2 = vec3(cir2.x, cir2.y, 1.);
    vec3 col3 = vec3(cir2.y, cir2.x, 0.5);

    float dist1 = length(fragCoord - res*cir1);
    float dist2 = length(fragCoord - res*cir2);
    float dist3 = length(fragCoord - res*cir3);

    vec3 bg = vec3(0.2*cos(iTime-fragCoord.xxx*.1));
    bg = 0.04 / bg;

    float cirR = .1*res.x;
    vec3 fc = bg;

    if(dist1 < cirR) fc = col1;
    if(dist2 < cirR) fc = col2;
    if(dist3 < cirR) fc = col3;

    fragColor = vec4(fc, 2.0);
}
