#define Main void mainImage(out vec4 Q, in vec2 U)
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
// circular  https://iquilezles.org/articles/smin/
float smin( float a, float b, float k )
{
    k *= 1.0/(1.0-sqrt(0.5));
    float h = max( k-abs(a-b), 0.0 )/k;
    return min(a,b) - k*0.5*(1.0+h-sqrt(1.0-h*(h-2.0)));
}
vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float map (vec3 p) {
    float s = .001;
    float m = .005;
    float d = abs(length(p-vec3(0,0,.1))-.52)-s;
    d = smin(d,abs(length(p-vec3(.4,.45,0))-.1)-s,m);
    d = smin(d,abs(length(p-vec3(.45,.31,0))-.05)-s,m);
    d = smin(d,abs(length(p-vec3(.52,.37,0))-.02)-s,m);
    d = smin(d,abs(length(p-vec3(.51,.36,0))-.02)-s,m);
    d = smin(d,abs(length(p-vec3(.52,.37,0))-.02)-s,m);
    d = smin(d,abs(length(p-vec3(.48,.24,0))-.02)-s,m);
    d = smin(d,abs(length(p-vec3(.27,.47,0))-.02)-s,m);
    d = smin(d,-p.z,2.*m);
    return d;

}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));

}
Main {
    Q = A(U);
    vec4 h = hash(vec4(iFrame,1,1,1));
    U = 2.*(U+h.xy-.5*R)/R.y;
    vec3 p = vec3(0,0,-2);
    vec3 d = normalize(vec3(U,3.));
    d.y = -d.y;
    p.yz *= ei(-.6);
    d.yz *= ei(-.6);
    p += h.z*d*map(p);
    for (float i = 0.; i < 150.; i++) {
        float m = map(p);
        p += d*max(abs(m),1e-6);
        if (p.z >0.) {
            d = normalize(reflect(d,normal(p))*.5+.2*normalize(hash(vec4(R*U,iFrame,i)).xyz*2.-1.));
            d.z = -abs(d.z);
        } 
        else 
        if (abs(m) < 1e-4) {
            float z = (hash(vec4(R*U,iFrame,i)).z*2.-1.);
            vec3 n = normal(p);
            d = z>0.7?reflect(d,n):normalize(d-.2*n);//refract(d,n,1.);
            p += .001*d;
        }

        if (p.z < -3.) {
            p += d*(p.z+3.);
            p += d*(p.z+3.);
            p += d*(p.z+3.);
            p += d*(p.z+3.);
            p += d*(p.z+3.);
            Q += 1.5*vec4(abs(p.x-2.5)<2.&&abs(p.y+2.5)<4.);
            Q += vec4(1,2,3,4)*vec4(abs(p.x+5.5)<1.&&abs(p.y+1.5)<2.);
            break;
        }
    }


}
