void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    vec2 circ_uv = fract(uv * 20.0) * 2.0 - 1.0;
    float circ_dist = length(circ_uv);
    float angle = 1.5707-atan(circ_uv.y, circ_uv.x);

    vec3 col;

    if (circ_dist > 0.9) {
        float dist = length(uv) * 10.0;
        float td = 1.0 / sqrt(1.0 - 2.0 / dist);
        //float _time = mod(iTime / td, 6.283 * td / 2.0) * td;

        col = hsv2rgb(vec3(td, 1.0, 1.0));
    }
    else {
        float dist = length(floor(uv * 20.0) / 20.0) * 10.0;
        float td = 1.0 / sqrt(1.0 - 2.0 / dist);
        vec3 clock = abs(mod(angle - iTime / td, 6.283)) < 0.2 ? vec3(0.0) : vec3(1.0);

        col = clock;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
