vec2 centerAlign(vec2 bgUv, vec2 bgSize, vec2 fgSize, vec2 anchor, vec2 fgScale) {
    return (bgUv*bgSize - anchor)/(fgSize*fgScale) + vec2(0.5);
}

mat2 rotate2d(float _angle) {
    float c = cos(_angle);
    float s = sin(_angle);
    return mat2(c, -s, s, c);
}

vec4 blendColor(vec2 iuv, sampler2D fgTexture, vec4 baseColor, vec2 bgSize, vec2 fgSize, vec2 anchorCenter, vec2 fgScale) {
    vec4 resultColor = baseColor;
    vec2 uv = centerAlign(iuv, bgSize, fgSize, anchorCenter, fgScale);

    float fgRatio = fgSize.x / fgSize.y;
    vec2 rotateCenter = vec2(0.5);
    uv -= rotateCenter;
    uv.y /= fgRatio;
    uv = rotate2d(radians(-iTime * 5.0)) * uv;
    uv.y *= fgRatio;
    uv += rotateCenter;

    resultColor = vec4(0.0);
    // Use texture2D with clamp to border to avoid if statement
    if (all(lessThan(vec2(0.0), uv))&&all((lessThan(uv, vec2(1.0))))) {
        uv.y = 1.0 - uv.y; // Flip the y-coordinate
        vec4 blendColor = texture(fgTexture, uv); // Shader will clamp uv coordinates by itself
        resultColor = mix(baseColor, blendColor, blendColor.a);
    }
    return resultColor;
}

vec2 getScale(vec2 bgSize, vec2 fgSize) {
    vec2 scale = bgSize / fgSize;
    return vec2(min(scale.x, scale.y));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 vTextureCoord = fragCoord.xy / iResolution.xy;
    vec2 bgSize = iResolution.xy;
    vec2 fgAnchor = bgSize * 0.5;
    vec2 fgSize = iChannelResolution[0].xy;

    vec2 scale = getScale(bgSize, fgSize);
    vec4 baseColor = vec4(0.0);
    vec4 resultColor = blendColor(vTextureCoord, iChannel0, baseColor, bgSize, fgSize, fgAnchor, scale);
    fragColor = resultColor;
}
