// Circle edge smoothing
const float SMOOTHING = 12.0;

// RGB
const vec3 BACKGROUND = vec3(0.04);

// Circle colours
const vec3 COL1 = vec3(0.34, 0.75, 0.29);
const vec3 COL2 = vec3(0.12, 0.7, 0.12);

// Total number of rows of circles
const float ROWS = 18.0;

float Circle(vec2 pos, float radius)
{
    return length(pos) - radius;
}

float Checkerboard(vec2 uv, vec2 total)
{
    uv = floor(total * uv);
	return mod(uv.x + uv.y, 2.);
}

// https://easings.net/#easeOutQuart
float EaseOutQuart(float x)
{
    return 1.0 - pow(1.0 - x, 4.0);
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalised pixel coordinates (from 0 to 1)
    vec2 texCoord = fragCoord / iResolution.xy;

    // Calculate aspect ratio
    float w = 1.0 / min(iResolution.x, iResolution.y);

    // Aspect ratio corrected UV coordinates
    vec2 uv = fragCoord * w;

    // Checkerboard
    float check = Checkerboard(uv, vec2(ROWS));

    // Position and number of circles
    vec2 pos = fract(uv * ROWS);
    pos -= 0.5;

    // Scale circle radius
    float radius = EaseOutQuart(texCoord.x * (1.0 - texCoord.y));
    radius = max(0.1, radius) * 0.5;

    // Get the distance to the circle centre
    float c = Circle(pos, radius);

    // Smoothing
    float s = SMOOTHING * w;
    c = smoothstep(s, -s, c);

    // Output
    vec3 col = mix(COL1, COL2, EaseOutQuart(texCoord.x));
    fragColour.rgb = mix(BACKGROUND, col, c * check);
    fragColour.a = 1.0;
}
