vec4 col = vec4(0.0);

vec4 circle(vec2 uv, vec3 color, vec2 center, float radius){
    float dist = length(uv - center); 
    if (dist < radius) { 
        col.rgb = color;
    }
    col.rgb = mix(color, col.rgb, 
        smoothstep(radius - 0.01, radius + 0.02, dist));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{  
    vec2 uv = fragCoord / iResolution.y - vec2(0.5, 0.0);
    uv.x += 0.1;

    float radius = 0.1 + 0.05 * sin(iTime * 1.0); 

    circle(uv, vec3(1.0, 0.0, 0.0),     vec2(sin(iTime+.1)/4.0, cos(iTime+.1)/4.0)+ 0.5, radius+ 0.35);    
    circle(uv, vec3(1.000,0.635,0.000), vec2(sin(iTime+.2)/4.0, cos(iTime+.2)/4.0)+ 0.5, radius+ 0.30);
    circle(uv, vec3(1.000,1.000,0.000), vec2(sin(iTime+.3)/4.0, cos(iTime+.3)/4.0)+ 0.5, radius+ 0.25);
    circle(uv, vec3(0.0, 1.0, 0.0),     vec2(sin(iTime+.4)/4.0, cos(iTime+.4)/4.0)+ 0.5, radius+ 0.20);    
    circle(uv, vec3(0.000,1.000,1.000), vec2(sin(iTime+.5)/4.0, cos(iTime+.5)/4.0)+ 0.5, radius+ 0.15);
    circle(uv, vec3(0.000,0.000,1.000), vec2(sin(iTime+.6)/4.0, cos(iTime+.6)/4.0)+ 0.5, radius+ 0.10);
    circle(uv, vec3(0.733,0.000,1.000), vec2(sin(iTime+.7)/4.0, cos(iTime+.7)/4.0)+ 0.5, radius+ 0.05);


    fragColor = col; 
}
