void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float prandom(float t){t+=fract(sin(t)*46372.)/100.;return fract(sin(t)*47453.5453);}
float pows(float a,float b){return pow(abs(a),b)*sign(a);}
float sine(float x){return sin(acos(-1.)*2.*x);}
float saw(float x){return(fract(x)*2.)-1.;}
float pulse(float x){return fract(x)>0.5?-1.:1.;}
float tri(float x){return(abs(mod((x-.25)*2.,2.)-1.)*2.)-1.;}
float fibonacci(float n){return((pow(1.+sqrt(5.),n))-(pow(1.-sqrt(5.),n)))/(pow(2.,n)*sqrt(5.));}
float curveSmooth(float x,float a){float b=pow(x*2.,a)/2.;if(x>0.5){b=1.-pow(2.-(x*2.),a)/2.;}return b;}
float fractSmooth(float x,float a){return(a>0.)?max(1.-(fract(x)/a),fract(x)):min(fract(x),fract(-x)/-a);}
float noise(float x){return(prandom(x)*2.)-1.;}
float noiseSmooth(float x,float p){return mix(noise(floor(x*p)),noise(floor(x*p)+1.),fract(x*p));}

