bool split(vec2 uv, float split){
    return mod(uv.x, split)>(split/2.0)^^mod(uv.y, split)>(split/2.0);
}
float spliff(vec2 uv, float splig){
    return split(uv,splig)?1.0:0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float scale = 5.0;
    uv*=scale;
    vec2 mousepos = iMouse.xy/iResolution.xy;
    vec3 col = vec3(spliff(uv, mousepos.x)+spliff(uv, mousepos.y)+spliff(uv, 0.1111111))/(3.0+(max(uv.x, uv.y)-min(uv.x,uv.y)));


    fragColor = vec4(col,1.0);
}
