vec3 ringset(in vec2 st, float m) {

    vec3 col = vec3(
        smoothstep(
            m, 
            m + .02, 
            length(vec2(st.x*.24, st.y-tan(iTime)*.1 ))
        ),
        smoothstep(
            m+.03, 
            m + .06,
            length(vec2(st.x*.24, st.y-cos(iTime*4.)*.1))
        ),
        smoothstep(
            m+.07, 
            m + .09, 
            length(vec2(st.x*.24, st.y-sin(iTime*4.)*.4 ))
        )
    );
    vec3 inv = 1.-col;
    return inv*col;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = (uv - vec2(.5,.5) ) * 2.;


    vec3 col = 
        ringset(st, .11)
        + ringset(st*.8, .10).brg
        + ringset(st*1.2, .13).gbr;


    fragColor = vec4(col*2.,1.0);
}
