void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float nLines = 1.;
    float duration = 15.;

    bool inv = sin(iTime) < 0.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float depth = texture(iChannel0, uv).r;
    vec3 col = vec3(0.);

    vec4 prevColor = texture(iChannel2, uv);// Color from the previous frame

    float t = fract(iTime / duration);
    float tDepth = 1. - t;

    float line = mod(abs(depth - tDepth), 1./nLines);

    float lineWidth = 1. / (nLines * 4./3.); // for black space in between

    if (line <= lineWidth) {
        // 0. .. .33 = red
        // .33 .. .66 = green
        // .66 .. 1. = blue
        float linePos = line / lineWidth;

        bool isFrontOfLine = mod(linePos,1./3.) <= lineWidth * 0.05;
        if (isFrontOfLine) {
            vec4 tex2 = texture(iChannel1, uv);
            if (inv) tex2.rgb = 1.-tex2.rgb;

            if (linePos < 1.0/3.0) {
                col.r = round(tex2.r);
            } else if (linePos < 2.0/3.0) {
                col.g = round(tex2.g);
            } else {
                col.b = round(tex2.b);
            }
        } else {
            col = prevColor.rgb;
        }

    } else {
        col = vec3(0.0);
    }

    fragColor = vec4(col, 1.0);
}
