// rounded box 2D with 4 individual rounding controls for the corners

float box4corner(vec2 p, vec2 b, vec4 cr) {
    vec2 s = step(p, vec2(0.0));
    float r = mix(
    mix(cr.y, cr.z, s.y),  
    mix(cr.x, cr.w, s.y),
    s.x);

   p = abs(p) - b + r;
   return length(max(p, 0.0)) + min(max(p.x, p.y), 0.0) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    vec2 pos = p;

    float r1 = (0.5+0.5*sin(iTime))*0.2;
    float r2 = (0.5+0.5*sin(iTime+2.0))*0.3;
    float r3 = (0.5+0.5*sin(iTime+7.0))*0.1;
    float r4 = (0.5+0.5*sin(iTime+11.0))*0.4;
    vec4 radii = vec4(r1, r2, r3, r4);

    float d = box4corner( pos, vec2(0.8, 0.4), radii);

	// coloring, iq style
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 )
    {
    pos = m;
	float d = box4corner( pos, vec2(0.8, 0.4), radii);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}
