#define R iResolution.xy
void mainImage( out vec4 O, in vec2 U )
{
    U /= R.xy;
    O =  texture(iChannel0, U);
}

vec2 mainSound( int samp, float time )
{

    return func01(time);
}

#define tau 6.2831
#define S(f) sin(tau*time*f)
#define A 4.  //4 is the "total chaos" parameter
#define F(x,a) a*x*(1.-x) //Verhulst logistic function

#define N pow(2.,1./12.)
vec2 func01(float time) {

    float R=0.,
          i=0.,x= .5;   
    // .5 is a repelling fixed point 

    //after 3 seconds choose an initial point that
    //has a more chaotic orbit
    x += mod(time,15.)>3.?.002*( mod(floor(time),20.)):0.;

    for (;i<10.;i++) {
        x = F(x,A);
        R +=  S( (300.*(pow(N,i)))*x ) / pow(1.3,i/2.);
    }

    return  
            2.*vec2(R)                       //waveform
            *vec2(cos(time),sin(time));      //pan

}

#define R iResolution.xy
void mainImage( out vec4 O, in vec2 U )
{
    vec2 uv = U/R.xy;

    U = (2.*U-R)/R.y*3.;

    vec2 cc = func01(iTime);

    float c1 = exp( -length( U-1.5*cc)*6.);

    cc *= cc;

    O = c1*vec4( vec3(0,.5,1)*cc.x + vec3(1,.5,0)*cc.y,0) 
        + .997*texture(iChannel0, uv);
}
