float Hash_2_1(vec2 p)
{
	p = fract(p*vec2(237.56, 589.011));
	p+= dot(p, p + 766.43);
	return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec4 col = vec4(0,0,0.1,0);


    vec2 uv = fragCoord/iResolution.xy;
    vec2 cenV = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float n = Hash_2_1(cenV);
    float twinkle = sin(iTime + n * 633.3964) * 0.5 + 0.5;
	float stars = pow(Hash_2_1(uv), 210.) * twinkle;
 	col+= stars;

    fragColor = col;
}
