#define PI 3.14159265359
#define TWO_PI 6.28318530718

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Adjust the coordinate system, with the origin at the center of the screen
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Calculate the polar coordinates
    float r = length(uv) * 2.0; // Spread the spiral over the screen
    float theta = atan(uv.y, uv.x);

    // Time variable for a pulsating beat effect
    float beat = sin(iTime) * 0.2 + 0.8;
    float time = iTime * beat;

    // Time variable to rotate the spiral
    float rotation = iTime * 3.0;

    // Modify theta for a secondary rotation effect
    theta += sin(rotation) * 0.5;

    // Spiral equation parameters with beat pulsation
    float a = 0.2 * beat; // Controls the spacing between each turn
    float b = 12.0; // Controls the growth rate of the spiral

    // Convert polar coordinates to spiral coordinates, applying rotation
    float spiral = mod(theta - b * sin(rotation) * r * r, TWO_PI);

    // Create a banding effect based on the distance from the spiral line
    float line = smoothstep(0.0, 0.1, abs(spiral - PI) * r);

    // Texture coordinates for sampling the external texture
    vec2 texCoords = vec2(0.5 + cos(theta) * 0.5, 0.5 + sin(theta) * 0.5) * beat;

    // Sample the external texture using the modified texture coordinates
    vec3 color = texture(iChannel0, texCoords).rgb;

    // Mix the color with the banding effect and apply the beat pulsation
    fragColor = mix(vec4(color, 1.0), vec4(0.0, 0.0, 0.0, 1.0), line * beat);
}
