void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates for 1920x1080 resolution
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    float sinValue = abs(sin(iTime - uv.y));
    float lengthY = length(uv.y);
    float siri = sinValue / (lengthY + 0.0001) * 0.05;

    vec3 color = siri * mix(vec3(0.2, 0.7, 0.4), vec3(0.2, 0.4, 1.0), abs(uv.x));

    // Output to screen
    fragColor = vec4(color, 1.0);
}
