void mainImage( out vec4 color, in vec2 uv )
{
    uv.x = (uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = (uv.y-0.5*iResolution.y)/iResolution.y;

    float factor = 10.+float(iFrame)*pow(1.01,float(iFrame));
    float uv_x = 0.5*(floor(factor*uv.x)/factor+ceil(factor*uv.x)/factor);
    float uv_y = 0.5*(floor(factor*uv.y)/factor+ceil(factor*uv.y)/factor);

    float l = pow(pow(uv_x,2.0)+pow(uv_y,2.0),0.5);


    vec3 col = vec3(0.);
    if(l<0.5){
    col = vec3(uv_x+cos(l*pow(factor,2.)),uv_y+cos(l*pow(factor,2.)),2.*l);
    }else{
    col = vec3(uv.x+cos(l*pow(factor,1.5)),uv.y+cos(l*pow(factor,1.5)),2.*l);
    }

    color = vec4(col,1.0);
}
