void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;

    float d = length(uv);

    float angle = iTime * .13 + d;
    for (int i = 0; i < 13; i++) {
        uv = (abs(uv) - .13 * float(i)) * mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    }
    vec3 col = vec3(fract(uv.x * 13.), fract(uv.x * 13.), sin(uv.x * 13.) + cos(uv.y * 13.));

    fragColor = vec4(col, 1.0);
}
