void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    if (distance(fragCoord, iMouse.xy) < 10.0 && iMouse.z > 0.5) {
        col = vec3(step(texture(iChannel1, uv).r,0.5));
    }

    if (texelFetch(iChannel2, ivec2(82,0),0).x == 1.0) {
        col = vec3(step(texture(iChannel1, uv).r,0.5));
    }

    col += texture(iChannel0, uv).rgb;

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color;
    float myself = texture(iChannel0, fragCoord/iResolution.xy).r;

    float total = 
    texture(iChannel0, (fragCoord - vec2(0.0, -1.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(1.0, -1.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(1.0, 0.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(1.0, 1.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(0.0, 1.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(-1.0, 1.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(-1.0, 0.0))/iResolution.xy).r
    + texture(iChannel0, (fragCoord - vec2(-1.0, -1.0))/iResolution.xy).r;



    if (myself == 1.0) {
        if (total >= 2.0 && total <= 3.0) {
            color = vec3(1.0);
        }
    }
    else {
        if (total == 3.0) {
            color = vec3(1.0);
        }
    }

    fragColor = vec4(color, 1.0);
}
