void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Left to right (-1 to 1)
    float leftToRight = getNormalizedLeftToRight(iTime)*2.-1.;

    float mask = max(0.,1.-length(uv-vec2(leftToRight,.1*sin(TAU*iTime))));

    // Pixel color
    vec3 col = vec3(.5+.5*uv,.5*(1.-leftToRight));

    // Output to screen
    fragColor = vec4(col*mask,1.0);
}

vec2 mainSound( int samp, float time )
{
    float baseWave = sin(TAU*440.*time)*.4;

    float right = getNormalizedLeftToRight(time);
    float left = 1.- right;

    return vec2(baseWave*left, baseWave*right);
}

#define TAU 6.28318530718
#define PI 3.14159265359

float getNormalizedLeftToRight(float time)
{
    return .5+.5*sin(PI*time);
}
