vec3 random_colour(int seed) {
    int red = (seed * 513) % 256;
    int blue = (seed * 724) % 256;
    int green = (seed * 93) % 256;

    return vec3(float(red) / 256.0, float(blue) / 256.0, float(green) / 256.0);
}

bool is_prime(int n) {
    if (n == 0 || n == 1) {
        return false;
    }

    if (n % 2 == 0) {
        return false;
    }

    for (int factor = 3; factor * factor <= n; factor += 2) {
        if (n % factor == 0) {
            return false;
        }
    }

    return true;
}

int seed_value(vec2 coordinates) {
    int coord_val = int(coordinates.x + iTime * 60.0) ^ int(iResolution.y - coordinates.y + iTime * 60.0);

    if (!is_prime(coord_val)) {
        return 0;
    } else {
        return coord_val;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Output to screen
    fragColor = vec4(random_colour(seed_value(fragCoord)), 1.0);
}
